import React from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { setFilterQuery } from '../actions';

function debounce(func, wait) {
    let timeout;
    return function (...args) {
        const context = this;
        const later = function () {
            timeout = null;
            func.apply(context, args);
        };
        clearTimeout(timeout);
        timeout = setTimeout(later, wait);
        if (!timeout) func.apply(context, args);
    };
}

function handleInput(dispatch) {
    debugger;
    const val = ev.target.value;
    dispatch(setFilterQuery(val));
};

handleInput = debounce(handleInput);

const Filter = React.memo(() => {
    const filterQuery = useSelector(state => state.user.filterQuery);
    const dispatch = useDispatch();
    return (
        <div>
            <input type="text" value={filterQuery || ''} onChange={() => handleInput(dispatch)}/>
        </div>
    );
});

export default Filter;
